
public class Nestedifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
    
    String s="heLlo";
    String s1="hello";
    if(s.equals(s1))
    {
        System.out.println("both are equal");
    }
    else {
        System.out.println("not equal");
        
    }
        
    String s11="nuCot";
    String s12="NUCOT";
    if(s11.equalsIgnoreCase(s12))
    {
        System.out.println("both are equal");
    }
    else {
        System.out.println("not equal");
        
    }
    
    
    
    
    
    
    
}
}

